mapper.update:
UPDATE TBL_FREE_BOARD
SET VIEW_COUNT = VIEW_COUNT + 1
WHERE FREE_BOARD_ID = #{freeBoardId}

UPDATE TBL_FREE_BOARD
SET TITLE = #{title} , CONTENT = #{content}
WHERE FREE_BOARD_ID = #{freeBoardId}

mapper.select:
SELECT RNUM, FREE_BOARD_ID, TITLE, VIEW_COUNT, REG_DATE, MOD_DATE, MEMBER_ID, LOGIN_ID, COMMENT_COUNT, LIKE_COUNT, FREE_FILE_ID, ORIGINAL_FILENAME, UUID, FILE_PATH, EXTENSION FROM ( SELECT ROWNUM AS RNUM, FREE_BOARD_ID, TITLE, VIEW_COUNT, REG_DATE, MOD_DATE, MEMBER_ID, LOGIN_ID, COMMENT_COUNT, LIKE_COUNT, FREE_FILE_ID, ORIGINAL_FILENAME, UUID, FILE_PATH, EXTENSION FROM ( SELECT FB.FREE_BOARD_ID, FB.TITLE, FB.VIEW_COUNT, FB.REG_DATE, FB.MOD_DATE, M.MEMBER_ID, M.LOGIN_ID, (SELECT COUNT(*) FROM TBL_FREE_COMMENT FC WHERE FC.FREE_BOARD_ID = FB.FREE_BOARD_ID) AS COMMENT_COUNT, (SELECT COUNT(*) FROM TBL_FREE_LIKE FL WHERE FL.FREE_BOARD_ID = FB.FREE_BOARD_ID) AS LIKE_COUNT, FF.FREE_FILE_ID, FF.ORIGINAL_FILENAME, FF.UUID, FF.FILE_PATH, FF.EXTENSION FROM TBL_FREE_BOARD FB JOIN TBL_MEMBER M ON FB.MEMBER_ID = M.MEMBER_ID LEFT JOIN TBL_FREE_FILE FF ON FB.FREE_BOARD_ID = FF.FREE_BOARD_ID
M.LOGIN_ID LIKE '%' || #{cond.keyword} || '%'
FB.TITLE LIKE '%' || #{cond.keyword} || '%'
FB.CONTENT LIKE '%' || #{cond.keyword} || '%'
ORDER BY FREE_BOARD_ID DESC )
WHERE ROWNUM <= #{page.page} * #{page.size}
) WHERE RNUM > (#{page.page} - 1) * #{page.size}
