mapper.select:
SELECT COUNT(*)
FROM TBL_FREE_COMMENT
WHERE FREE_BOARD_ID = #{freeBoardId}

mapper.update:
UPDATE TBL_FREE_COMMENT
SET CONTENT = #{content}, MOD_DATE = SYSDATE
WHERE FREE_COMMENT_ID = #{freeCommentId}

mapper.select:
SELECT RNUM, FREE_COMMENT_ID, FREE_BOARD_ID, CONTENT, REG_DATE, MOD_DATE, MEMBER_ID, LOGIN_ID FROM ( SELECT ROWNUM AS RNUM, FREE_COMMENT_ID, FREE_BOARD_ID, CONTENT, REG_DATE, MOD_DATE, MEMBER_ID, LOGIN_ID FROM ( SELECT FC.FREE_COMMENT_ID, FC.FREE_BOARD_ID, FC.CONTENT, FC.REG_DATE, FC.MOD_DATE, M.MEMBER_ID, M.LOGIN_ID FROM TBL_FREE_COMMENT FC JOIN TBL_MEMBER M ON FC.MEMBER_ID = M.MEMBER_ID WHERE FC.FREE_BOARD_ID = #{freeBoardId} ORDER BY FC.FREE_COMMENT_ID DESC )
WHERE ROWNUM <= #{page.page} * #{page.size} + 1
) WHERE RNUM > (#{page.page} - 1) * #{page.size}
